mapper.select:
SELECT stop.stop_id , stop.stop_name , stop.x as longitude , stop.y as latitude , stop.city_code , city.city_name FROM tb_stops stop, tb_city_code city WHERE stop.city_code = city.city_code
ORDER BY
stop.stop_id ${sortType}
stop.stop_name ${sortType}
stop.city_code ${sortType}
stop.stop_id desc
stop.stop_id desc
LIMIT #{startPageLimit}, #{endPageLimit}

mapper.update:
UPDATE
	tb_stops_task
SET
	stop_name = #{stopName}
	, x = #{longitude}
	, y = #{latitude}
	, level = #{level}
	, nonstop_yn = #{nonstopYn}
	, city_code = #{cityCode}
WHERE
	task_id = #{taskId}

UPDATE
	tb_bus_stops_info_task
SET
	virtual_stop_yn = #{virtualStopYn}
	, center_stop_yn = #{centerStopYn}
	, do = #{sido}
	, gu = #{gu}
	, dong = #{dong}
	, road_address = #{roadAddress}
	, road_name = #{roadName}
	, bonbun = #{bonbun}
	, boobun = #{boobun}
	, display_id = #{displayId}
	, provider_id = #{providerId}
WHERE
	task_id = #{taskId}

UPDATE
	tb_bus_stop_mapping_task
SET
	local_stop_id = #{localStopId}
	, provider_id = #{providerId}
	, display_id = #{displayId}
WHERE
	task_id = #{taskId}
